import WelcomePage from "./component/welcomepage/WelcomePage";
import Profile from "./component/welcomepage/Profile";
const data = [
  {
    userName: "nomin",
    password: "nnnn",
  },
  {
    userName: "naraa",
    password: "aaaa",
  },
  {
    userName: "bataa",
    password: "bbbb",
  },
  {
    userName: "sukhee",
    password: "ssss",
  },
];
function App() {
  const [isLoggedIn, setIsLoggedIn] = useState(false);

  function loginHandler(userName, password) {
    console.log("username:", userName);
    console.log("password:", password);
    if (userName === data.userName[i] && password === data.password[i]) {
      setIsLoggedIn(true);
    } else {
      console.error("wrong username or password");
    }
  }
  return (
    <div className="App">
      {isLoggedIn ? <Profile /> : <WelcomePage setLogin={loginHandler} />}
    </div>
  );
}
export default App;
