function App() {
  const [count, setCount] = useState("");
  const [currentSum, setCurrentSum] = useState(0);
  const changeToOne = () => {
    setCount("1");
  };
  const changeToTwo = () => {
    setCount("2");
  };
  const changeToThree = () => {
    setCount("3");
  };
  const changeToFour = () => {
    setCount("4");
  };
  const changeToFive = () => {
    setCount("5");
  };
  const changeToSix = () => {
    setCount("6");
  };
  const changeToSeven = () => {
    setCount("7");
  };
  const changeToEight = () => {
    setCount("8");
  };
  const changeToNine = () => {
    setCount("9");
  };
  const changeToZero = () => {
    setCount("0");
  };
  const Add = (e) => {
    e.preventDefault();
    let currentNum = document.querySelector("input").value;
    if (currentNum == "") return;
    let sum = currentSum + parseInt(currentNum);
    setCurrentSum(sum);
    document.querySelector("input").value = "";
  };
  return (
    <div className="App">
      <input value={count} />
      <button onClick={changeToOne}>1</button>
      <button onClick={changeToTwo}>2</button>
      <button onClick={changeToThree}>3</button>
      <button onClick={changeToFour}>4</button>
      <button onClick={changeToFive}>5</button>
      <button onClick={changeToSix}>6</button>
      <button onClick={changeToSeven}>7</button>
      <button onClick={changeToEight}>8</button>
      <button onClick={changeToNine}>9</button>
      <button onClick={changeToZero}>0</button>
      <button onClick={Add}>+</button>
    </div>
  );
}
export default App;
